import React from "react";
import { useNavigate } from "react-router-dom";

const CategoriesPage = () => {
  const navigate = useNavigate();

  const categories = ["Technology", "Sports", "Politics", "Business", "Health", "Entertainment"];

  const handleCategoryClick = (category) => {
    navigate(`/dashboard`);
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>Categories</h1>
      <div style={styles.categories}>
        {categories.map((category) => (
          <button
            key={category}
            style={styles.categoryButton}
            onClick={() => handleCategoryClick(category)}
          >
            {category}
          </button>
        ))}
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  categories: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "15px",
  },
  categoryButton: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "0.3s",
    border: "none",
  },
};

export default CategoriesPage;
