import React, { useState } from "react";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav style={styles.navbar}>
      {/* Logo */}
      <div style={styles.logo}>ðŸ“° News Summa</div>

      {/* Desktop Menu */}
      <ul style={{ ...styles.navLinks, display: isOpen ? "flex" : "none" }}>
        <li style={styles.navItem}><a href="/" style={styles.navLink}>Home</a></li>
        <li style={styles.navItem}><a href="/categories" style={styles.navLink}>Categories</a></li>
        <li style={styles.navItem}><a href="/about" style={styles.navLink}>About</a></li>
        <li style={styles.navItem}><a href="/contact" style={styles.navLink}>Contact</a></li>
      </ul>

      {/* Login/Signup Button */}
      <div style={styles.authButtons}>
        <button style={styles.loginBtn}>Login</button>
        <button style={styles.signupBtn}>Sign Up</button>
      </div>

      {/* Hamburger Menu (Mobile View) */}
      <div style={styles.hamburger} onClick={() => setIsOpen(!isOpen)}>
        â˜°
      </div>
    </nav>
  );
};

// **Minimalist Black & White Styling**
const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "15px 30px",
    backgroundColor: "#111",
    color: "#fff",
    fontFamily: "'Arial', sans-serif",
    position: "sticky",
    top: "0",
    width: "100%",
    zIndex: "1000",
    boxShadow: "0px 4px 8px rgba(255, 255, 255, 0.1)",
  },
  logo: {
    fontSize: "1.5rem",
    fontWeight: "bold",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  navLinks: {
    display: "flex",
    gap: "20px",
    listStyle: "none",
  },
  navItem: {
    display: "inline",
  },
  navLink: {
    color: "#fff",
    textDecoration: "none",
    fontSize: "1rem",
    fontWeight: "bold",
    padding: "5px 10px",
    transition: "0.3s",
  },
  authButtons: {
    display: "flex",
    gap: "10px",
  },
  loginBtn: {
    backgroundColor: "transparent",
    color: "#fff",
    border: "1px solid #fff",
    padding: "8px 12px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "0.3s",
  },
  signupBtn: {
    backgroundColor: "#fff",
    color: "#000",
    border: "none",
    padding: "8px 12px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "0.3s",
  },
  hamburger: {
    fontSize: "1.5rem",
    cursor: "pointer",
    display: "none",
  },
};

// **Media Query for Mobile View**
if (window.innerWidth < 768) {
  styles.navLinks.display = "none";
  styles.hamburger.display = "block";
  styles.authButtons.display = "none";
}

export default Navbar;
