import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const NewsDetailsPage = () => {
  const location = useLocation();
  const { title, image } = location.state || {};

  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSummary = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5000/api/news/summary?title=${encodeURIComponent(title)}`);
        const data = await response.json();
        setSummary(data.summary || "No summary available.");
      } catch (error) {
        console.error("Error fetching news summary:", error);
        setSummary("An error occurred while fetching the summary.");
      } finally {
        setLoading(false);
      }
    };

    if (title) {
      fetchSummary();
    }
  }, [title]);

  if (!title) {
    return (
      <div style={styles.page}>
        <p style={styles.message}>No news selected.</p>
      </div>
    );
  }

  return (
    <div style={styles.page}>
      <img src={image || "https://via.placeholder.com/800x400"} alt="News" style={styles.image} />
      <div style={styles.content}>
        <h1 style={styles.title}>{title}</h1>
        <div style={styles.summaryContainer}>
          {loading ? <p style={styles.loading}>Loading summary...</p> : <p style={styles.summary}>{summary}</p>}
        </div>
        <div style={styles.links}>
          <a
            href={`https://www.google.com/search?q=${encodeURIComponent(title)}`}
            target="_blank"
            rel="noopener noreferrer"
            style={styles.link}
          >
            Read Full News
          </a>
          <a
            href={`https://www.youtube.com/results?search_query=${encodeURIComponent(title)}`}
            target="_blank"
            rel="noopener noreferrer"
            style={styles.link}
          >
            Watch Related Video
          </a>
        </div>
      </div>
    </div>
  );
};

const styles = {
  page: {
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    fontFamily: "Arial, sans-serif",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  image: {
    width: "100%",
    maxHeight: "500px",
    objectFit: "cover",
  },
  content: {
    maxWidth: "900px",
    width: "100%",
    padding: "40px 20px",
  },
  title: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "20px",
    borderBottom: "2px solid #444",
    paddingBottom: "10px",
  },
  summaryContainer: {
    fontSize: "1.2rem",
    lineHeight: "1.7",
    color: "#ccc",
    marginBottom: "30px",
  },
  loading: {
    fontSize: "1rem",
    color: "#888",
  },
  summary: {
    whiteSpace: "pre-wrap",
  },
  links: {
    display: "flex",
    gap: "20px",
    flexWrap: "wrap",
  },
  link: {
    backgroundColor: "#fff",
    color: "#000",
    padding: "12px 18px",
    borderRadius: "5px",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "background 0.3s",
  },
  message: {
    color: "#ccc",
    marginTop: "100px",
    fontSize: "1.5rem",
  },
};

export default NewsDetailsPage;
