import React, { useState } from "react";

const DashboardPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newsData, setNewsData] = useState([]);
  const [error, setError] = useState("");
  const [showVideoLink, setShowVideoLink] = useState(null); // Track visibility of YouTube link
  const [videoData, setVideoData] = useState({}); // Store video data for each news item

  // Categories for quick selection
  const categories = ["Technology", "Sports", "Politics", "Business", "Health", "Entertainment"];

  // Fetch news from your deployed API
  const fetchNews = async (query) => {
    setError("");
    setNewsData([]);
    setSearchTerm(query);

    try {
      const response = await fetch(`https://newsai-tibe.onrender.com/api/news/${query}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
        mode: "cors", // Make sure your API supports CORS
      });

      if (!response.ok) {
        throw new Error(`HTTP Error: ${response.status}`);
      }

      const data = await response.json();
      setNewsData(data.articles || []);
    } catch (err) {
      console.error("Error fetching news:", err);
      setError("Failed to fetch news. Try again.");
    }
  };

  // Fetch YouTube video for a specific news title
  const fetchYouTubeVideo = async (title, index) => {
    try {
      const youtubeUrl = `https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(title)}&key=YOUR_YOUTUBE_API_KEY`;
      const response = await fetch(youtubeUrl);
      const data = await response.json();

      if (data.items && data.items.length > 0) {
        setVideoData((prevState) => ({
          ...prevState,
          [index]: data.items[0], // Store only the first video suggestion
        }));
      }
    } catch (error) {
      console.error("Error fetching YouTube videos:", error);
    }
  };

  // Handle search manually
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim() === "") return;
    fetchNews(searchTerm);
  };

  // Toggle the visibility of the YouTube link for each news item
  const toggleVideoLink = (index, title) => {
    if (videoData[index]) {
      setShowVideoLink((prevIndex) => (prevIndex === index ? null : index));
    } else {
      fetchYouTubeVideo(title, index); // Fetch YouTube video if not fetched yet
      setShowVideoLink(index); // Show video link after fetching
    }
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>ðŸ“° News Dashboard</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={styles.searchContainer}>
        <input
          type="text"
          placeholder="Search for news..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Search</button>
      </form>

      {/* Categories */}
      <div style={styles.categories}>
        <h3 style={styles.sectionTitle}>Categories</h3>
        <div style={styles.buttonContainer}>
          {categories.map((category) => (
            <button key={category} style={styles.categoryBtn} onClick={() => fetchNews(category)}>
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* News Results */}
      <div style={styles.results}>
        {error && <p style={styles.error}>{error}</p>}
        {newsData.length > 0 && (
          <div>
            <h3 style={styles.sectionTitle}>News Summaries</h3>
            <div style={styles.newsGrid}>
              {newsData.map((news, index) => (
                <div key={index} style={styles.newsCard}>
                  <img src={news.image} alt="News" style={styles.newsImage} />
                  <h4>{news.title}</h4>
                  <p>{news.summary}</p>
                  <a href={news.url} target="_blank" rel="noopener noreferrer" style={styles.newsLink}>
                    Read Full News â†’
                  </a>
                  <button
                    onClick={() => toggleVideoLink(index, news.title)}
                    style={styles.videoButton}
                  >
                    {showVideoLink === index ? "Hide Related Video" : "Show Related Video"}
                  </button>
                  {showVideoLink === index && videoData[index] && (
                    <a
                      href={`https://www.youtube.com/watch?v=${videoData[index].id.videoId}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      style={styles.youtubeLink}
                    >
                      ðŸŽ¥ Watch Related Video
                    </a>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// **Styling**
const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  searchContainer: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    width: "300px",
    borderRadius: "5px",
    border: "1px solid #888",
    backgroundColor: "#222",
    color: "#fff",
    fontSize: "1rem",
  },
  button: {
    padding: "10px 15px",
    marginLeft: "10px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    border: "1px solid #fff",
    cursor: "pointer",
    fontWeight: "bold",
    transition: "0.3s",
  },
  buttonContainer: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "10px",
    marginBottom: "20px",
  },
  categories: {
    marginBottom: "20px",
  },
  categoryBtn: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    backgroundColor: "transparent",
    color: "#fff",
    cursor: "pointer",
    transition: "0.3s",
  },
  results: {
    marginTop: "20px",
  },
  error: {
    color: "#ff4d4f",
    fontWeight: "bold",
  },
  newsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "15px",
    justifyContent: "center",
  },
  newsCard: {
    backgroundColor: "#222",
    padding: "15px",
    borderRadius: "5px",
    boxShadow: "0 2px 4px rgba(255, 255, 255, 0.1)",
    textAlign: "left",
  },
  newsImage: {
    width: "100%",
    height: "150px",
    objectFit: "cover",
    borderRadius: "5px",
    marginBottom: "10px",
  },
  newsLink: {
    display: "block",
    marginTop: "10px",
    color: "#fff",
    textDecoration: "none",
    fontWeight: "bold",
    borderBottom: "1px solid #fff",
    width: "fit-content",
  },
  youtubeLink: {
    display: "block",
    marginTop: "5px",
    color: "#ff0000",
    textDecoration: "none",
    fontWeight: "bold",
  },
  videoButton: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    backgroundColor: "transparent",
    color: "#fff",
    cursor: "pointer",
    transition: "0.3s",
    marginTop: "10px",
  },
};

export default DashboardPage;
