import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const DashboardPage = () => {
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  const queryParams = new URLSearchParams(location.search);
  const category = queryParams.get("category");

  useEffect(() => {
    const fetchNews = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5000/api/news?category=${category}`);
        const data = await response.json();
        setNewsData(data.articles || []);
      } catch (error) {
        console.error("Error fetching news:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [category]);

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>{category} News</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div style={styles.newsGrid}>
          {newsData.map((news, index) => (
            <div key={index} style={styles.newsCard}>
              <h3>{news.title}</h3>
              <p>{news.summary}</p>
              <a href={news.url} target="_blank" rel="noopener noreferrer" style={styles.link}>
                Read Full Article
              </a>
              <a
                href={`https://www.youtube.com/results?search_query=${encodeURIComponent(news.title)}`}
                target="_blank"
                rel="noopener noreferrer"
                style={styles.link}
              >
                Watch Related Video
              </a>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  newsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "15px",
  },
  newsCard: {
    backgroundColor: "#222",
    padding: "15px",
    borderRadius: "5px",
    textAlign: "left",
  },
  link: {
    color: "#00bcd4",
    textDecoration: "none",
    fontWeight: "bold",
  },
};

export default DashboardPage;
