import React, { useState } from "react";

const DashboardPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newsData, setNewsData] = useState([]);
  const [error, setError] = useState("");

  // Categories for quick access
  const fetchNews = async (query) => {
    setError("");
    setNewsData([]);
    
    try {
      console.log("Fetching news for:", query); // Debugging
  
      const response = await fetch(`https://newsai-tibe.onrender.com/news/${encodeURIComponent(query)}`);
      const data = await response.json();
      
      console.log("API Response:", data);  // Debugging response
  
      if (!data.articles || data.articles.length === 0) {
        setError("No news found for this search.");
      }
  
      setNewsData(data.articles || []);
    } catch (err) {
      console.error("Error fetching news:", err);
      setError("Failed to fetch news. Try again.");
    }
  };
  
  // Handle Search
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim() === "") {
      setError("Please enter a search term.");
      return;
    }
    fetchNews(searchTerm.trim());
  };
  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>ðŸ“° News Dashboard</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={styles.searchContainer}>
        <input
          type="text"
          placeholder="Search for news..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Search</button>
      </form>

      {/* Suggested Prompts */}
      <div style={styles.suggestions}>
        <h3 style={styles.sectionTitle}>Try These</h3>
        <div style={styles.buttonContainer}>
          {["AI Innovations", "Startup News", "Global Economy"].map((suggestion) => (
            <button key={suggestion} style={styles.suggestionBtn} onClick={() => fetchNews(suggestion)}>
              {suggestion}
            </button>
          ))}
        </div>
      </div>

      {/* Categories */}
      <div style={styles.categories}>
        <h3 style={styles.sectionTitle}>Categories</h3>
        <div style={styles.buttonContainer}>
          {categories.map((category) => (
            <button key={category} style={styles.categoryBtn} onClick={() => fetchNews(category)}>
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* News Results */}
      <div style={styles.results}>
        {error && <p style={styles.error}>{error}</p>}
        {newsData.length > 0 && (
          <div>
            <h3 style={styles.sectionTitle}>Search Results</h3>
            <div style={styles.newsGrid}>
              {newsData.map((news, index) => (
                <div key={index} style={styles.newsCard}>
                  <h4>{news.title}</h4>
                  <p>{news.description}</p>
                  <a href={news.url} target="_blank" rel="noopener noreferrer" style={styles.newsLink}>
                    Read More â†’
                  </a>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// **Minimalist Black & White Styling**
const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  searchContainer: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    width: "300px",
    borderRadius: "5px",
    border: "1px solid #888",
    backgroundColor: "#222",
    color: "#fff",
    fontSize: "1rem",
  },
  button: {
    padding: "10px 15px",
    marginLeft: "10px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    border: "1px solid #fff",
    cursor: "pointer",
    fontWeight: "bold",
    transition: "0.3s",
  },
  buttonContainer: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "10px",
    marginBottom: "20px",
  },
  suggestionBtn: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    backgroundColor: "transparent",
    color: "#fff",
    cursor: "pointer",
    transition: "0.3s",
  },
  suggestionBtnHover: {
    backgroundColor: "#fff",
    color: "#000",
  },
  categories: {
    marginBottom: "20px",
  },
  categoryBtn: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    backgroundColor: "transparent",
    color: "#fff",
    cursor: "pointer",
    transition: "0.3s",
  },
  categoryBtnHover: {
    backgroundColor: "#fff",
    color: "#000",
  },
  sectionTitle: {
    textTransform: "uppercase",
    fontSize: "1.2rem",
    marginBottom: "10px",
    letterSpacing: "1px",
    borderBottom: "2px solid #fff",
    display: "inline-block",
    paddingBottom: "5px",
  },
  results: {
    marginTop: "20px",
  },
  error: {
    color: "#ff4d4f",
    fontWeight: "bold",
  },
  newsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "15px",
    justifyContent: "center",
  },
  newsCard: {
    backgroundColor: "#222",
    padding: "15px",
    borderRadius: "5px",
    boxShadow: "0 2px 4px rgba(255, 255, 255, 0.1)",
  },
  newsLink: {
    display: "block",
    marginTop: "10px",
    color: "#fff",
    textDecoration: "none",
    fontWeight: "bold",
    borderBottom: "1px solid #fff",
    width: "fit-content",
  },
};

export default DashboardPage;
