import React from "react";
import { useNavigate } from "react-router-dom";

const HomePage = () => {
  const navigate = useNavigate();

  const handleLogin = () => {
    navigate("/login");
  };

  const handleSignup = () => {
    navigate("/signup");
  };

  return (
    <div style={styles.page}>
      {/* Hero Section */}
      <div style={styles.hero}>
        <h1 style={styles.headline}>Welcome to News Summarizer</h1>
        <p style={styles.description}>
          Simplify your news consumption with concise summaries. Stay informed, save time, and explore the topics that matter most.
        </p>
        <div style={styles.ctaButtons}>
          <button onClick={handleLogin} style={styles.ctaButton}>
            Login
          </button>
          <button onClick={handleSignup} style={styles.ctaButtonOutline}>
            Sign Up
          </button>
        </div>
      </div>

      {/* Explore Section */}
      <div style={styles.explore}>
        <h2 style={styles.sectionTitle}>Why Choose Us?</h2>
        <p style={styles.exploreText}>
          News Summarizer provides quick and accurate summaries of the latest news, saving you time and keeping you informed. Explore categories, summaries, and more after logging in or signing up.
        </p>
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  hero: {
    padding: "50px 20px",
    borderRadius: "10px",
    marginBottom: "30px",
    color: "#fff",
    background: "linear-gradient(to right, #6a11cb, #2575fc)",
  },
  headline: {
    fontSize: "3rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  description: {
    fontSize: "1.2rem",
    marginBottom: "30px",
    lineHeight: "1.6",
  },
  ctaButtons: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
  },
  ctaButton: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
    cursor: "pointer",
    border: "none",
  },
  ctaButtonOutline: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "transparent",
    color: "#fff",
    border: "2px solid #fff",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
    cursor: "pointer",
  },
  explore: {
    marginTop: "40px",
    padding: "20px",
    backgroundColor: "#111",
    borderRadius: "10px",
  },
  sectionTitle: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "1.5px",
  },
  exploreText: {
    fontSize: "1rem",
    marginBottom: "20px",
    lineHeight: "1.5",
  },
  exploreButtons: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
  },
  exploreButton: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
    cursor: "pointer",
    border: "none",
  },
  exploreButtonOutline: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "transparent",
    color: "#fff",
    border: "2px solid #fff",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
    cursor: "pointer",
  },
};

export default HomePage;
