import React, { useState } from "react";

const DashboardPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newsData, setNewsData] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false); // Loading state
  const [currentPage, setCurrentPage] = useState(1); // Pagination state
  const [articlesPerPage] = useState(5); // Articles per page
  const [savedArticles, setSavedArticles] = useState([]); // Saved articles

  const categories = ["Technology", "Sports", "Politics", "Business", "Health", "Entertainment"];

  // Fetch summarized news from API
  const fetchSummarizedNews = async (query) => {
    setError("");
    setNewsData([]);
    setLoading(true);

    try {
      const response = await fetch(`http://localhost:5000/api/summarize`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email: "test@example.com", password: "password123", query }),
      });

      if (!response.ok) {
        throw new Error(`HTTP Error: ${response.status}`);
      }

      const data = await response.json();
      setNewsData(data.articles || []);
    } catch (err) {
      console.error("Error fetching summarized news:", err);
      setError("Failed to fetch summarized news. Try again.");
    } finally {
      setLoading(false);
    }
  };

  // Handle search manually
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim() === "") return;
    fetchSummarizedNews(searchTerm);
  };

  // Save an article for later
  const saveArticle = (article) => {
    setSavedArticles((prev) => [...prev, article]);
  };

  // Pagination logic
  const indexOfLastArticle = currentPage * articlesPerPage;
  const indexOfFirstArticle = indexOfLastArticle - articlesPerPage;
  const currentArticles = newsData.slice(indexOfFirstArticle, indexOfLastArticle);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>ðŸ“° News Summarizer</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={styles.searchContainer}>
        <input
          type="text"
          placeholder="Search for news..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Search</button>
      </form>

      {/* Categories */}
      <div style={styles.categories}>
        <h3 style={styles.sectionTitle}>Categories</h3>
        <div style={styles.buttonContainer}>
          {categories.map((category) => (
            <button key={category} style={styles.categoryBtn} onClick={() => fetchSummarizedNews(category)}>
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* Loading State */}
      {loading && <p style={styles.loading}>Loading news...</p>}

      {/* Error Message */}
      {error && <p style={styles.error}>{error}</p>}

      {/* News Results */}
      {newsData.length > 0 && (
        <div>
          <h3 style={styles.sectionTitle}>News Summaries</h3>
          <div style={styles.newsGrid}>
            {currentArticles.map((news, index) => (
              <div key={index} style={styles.newsCard}>
                <img src={news.image} alt="News" style={styles.newsImage} />
                <h4>{news.title}</h4>
                <p style={styles.newsSummary}>
                  {news.summary.length > 300 ? `${news.summary.substring(0, 300)}...` : news.summary}
                </p>
                <button
                  onClick={() => window.open(news.url, "_blank")}
                  style={styles.newsLink}
                >
                  Read Full News â†’
                </button>
                <button
                  onClick={() => saveArticle(news)}
                  style={styles.saveButton}
                >
                  Save for Later
                </button>
              </div>
            ))}
          </div>

          {/* Pagination */}
          <div style={styles.pagination}>
            {Array.from({ length: Math.ceil(newsData.length / articlesPerPage) }, (_, i) => (
              <button
                key={i + 1}
                onClick={() => paginate(i + 1)}
                style={{
                  ...styles.pageButton,
                  backgroundColor: currentPage === i + 1 ? "#fff" : "transparent",
                  color: currentPage === i + 1 ? "#000" : "#fff",
                }}
              >
                {i + 1}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Saved Articles */}
      {savedArticles.length > 0 && (
        <div style={styles.savedArticles}>
          <h3 style={styles.sectionTitle}>Saved Articles</h3>
          <ul>
            {savedArticles.map((article, index) => (
              <li key={index} style={styles.savedArticle}>
                <a href={article.url} target="_blank" rel="noopener noreferrer" style={styles.savedLink}>
                  {article.title}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

// **Styling**
const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2rem",
    fontWeight: "bold",
