    borderBottom: "1px solid #fff",
    width: "fit-content",
  },
  saveButton: {
    marginTop: "10px",
    padding: "8px 12px",
    borderRadius: "5px",
    backgroundColor: "#00bcd4",
    color: "#fff",
    border: "none",
    cursor: "pointer",
    fontWeight: "bold",
    transition: "0.3s",
  },
  pagination: {
    marginTop: "20px",
    display: "flex",
    justifyContent: "center",
    gap: "10px",
  },
  pageButton: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    cursor: "pointer",
    transition: "0.3s",
  },
  savedArticles: {
    marginTop: "30px",
  },
  savedArticle: {
    marginBottom: "10px",
  },
  savedLink: {
    color: "#00bcd4",
    textDecoration: "none",
    fontWeight: "bold",
  },
