import React from "react";

const AboutPage = () => {
  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>About News Summarizer</h1>
      <p style={styles.description}>
        News Summarizer is a platform designed to provide concise and accurate summaries of the latest news. Our goal is to save your time by delivering the essence of news articles in just a few lines. Whether you're interested in technology, sports, politics, or entertainment, we've got you covered.
      </p>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  description: {
    fontSize: "1.2rem",
    lineHeight: "1.6",
  },
};

export default AboutPage;
