import { useState } from "react";

function Dashboard() {
  const [search, setSearch] = useState("");

  return (
    <div className="dashboard">
      <h2>Welcome to the News Summarizer</h2>
      <input
        type="text"
        placeholder="Search for news..."
        className="search-bar"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <NewsCategories />
      <NewsSuggestions />
    </div>
  );
}

function NewsCategories() {
  const categories = ["Technology", "Business", "Sports", "Health"];
  return (
    <div className="categories">
      {categories.map((category) => (
        <button key={category} className="category-btn">
          {category}
        </button>
      ))}
    </div>
  );
}

function NewsSuggestions() {
  const newsItems = [
    { title: "Tech Innovation 2024", img: "https://via.placeholder.com/200" },
    { title: "Stock Market Insights", img: "https://via.placeholder.com/200" },
    { title: "Olympics Highlights", img: "https://via.placeholder.com/200" },
  ];
  return (
    <div className="news-section">
      {newsItems.map((news, index) => (
        <div key={index} className="news-item">
          <img src={news.img} alt={news.title} />
          <h3>{news.title}</h3>
        </div>
      ))}
    </div>
  );
}

export default Dashboard;
