import React from "react";
import { Link } from "react-router-dom";

const HomePage = () => {
  return (
    <div style={styles.page}>
      {/* Hero Section */}
      <div style={styles.hero}>
        <h1 style={styles.headline}>Welcome to News Summarizer</h1>
        <p style={styles.description}>
          Stay updated with the latest news, summarized for your convenience. Explore categories, read summaries, and dive deeper into the topics that matter to you.
        </p>
        <div style={styles.ctaButtons}>
          <Link to="/dashboard" style={styles.ctaButton}>
            Get Started
          </Link>
          <Link to="/about" style={styles.ctaButtonOutline}>
            Learn More
          </Link>
        </div>
      </div>

      {/* Navigation Links */}
      <div style={styles.navLinks}>
        <Link to="/about" style={styles.link}>About</Link>
        <Link to="/categories" style={styles.link}>Categories</Link>
        <Link to="/contact" style={styles.link}>Contact</Link>
        <Link to="/dashboard" style={styles.link}>Dashboard</Link>
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  hero: {
    background: "linear-gradient(to right, #6a11cb, #2575fc)",
    padding: "50px 20px",
    borderRadius: "10px",
    marginBottom: "30px",
    color: "#fff",
  },
  headline: {
    fontSize: "3rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  description: {
    fontSize: "1.2rem",
    marginBottom: "30px",
    lineHeight: "1.6",
  },
  ctaButtons: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
  },
  ctaButton: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
    cursor: "pointer",
  },
  ctaButtonOutline: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "transparent",
    color: "#fff",
    border: "2px solid #fff",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
    cursor: "pointer",
  },
  navLinks: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
    flexWrap: "wrap",
  },
  link: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
    cursor: "pointer",
  },
};

export default HomePage;
