import React from "react";

const CategoriesPage = () => {
  const categories = ["Technology", "Sports", "Politics", "Business", "Health", "Entertainment"];

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>News Categories</h1>
      <div style={styles.categories}>
        {categories.map((category) => (
          <div key={category} style={styles.categoryCard}>
            <h3>{category}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  categories: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "15px",
    justifyContent: "center",
  },
  categoryCard: {
    backgroundColor: "#222",
    padding: "15px",
    borderRadius: "5px",
    textAlign: "center",
    fontWeight: "bold",
    boxShadow: "0 2px 4px rgba(255, 255, 255, 0.1)",
  },
};

export default CategoriesPage;
