import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const DashboardPage = () => {
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  const queryParams = new URLSearchParams(location.search);
  const category = queryParams.get("category");

  useEffect(() => {
    if (!category) {
      console.error("Category is missing in the URL");
      return;
    }

    const fetchNews = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5000/api/news/${category}`);
        const data = await response.json();

        const dummyData = (data.articles || []).map((article) => ({
          ...article,
          summary: article.summary || "This is a dummy summary for the article.",
        }));

        setNewsData(dummyData);
      } catch (error) {
        console.error("Error fetching news:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [category]);

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>{category ? `${category} News` : "News"}</h1>
      {loading ? (
        <p>Loading...</p>
      ) : newsData.length === 0 ? (
        <p>No news articles available for this category.</p>
      ) : (
        <div style={styles.newsGrid}>
          {newsData.map((news, index) => (
            <div key={index} style={styles.newsCard}>
              <img
                src={news.image || "https://via.placeholder.com/300"}
                alt={news.image ? "News Image" : "Placeholder Image"}
                style={styles.newsImage}
              />
              <h3>{news.title}</h3>
              <p>{news.summary}</p>
              <a href={news.url} target="_blank" rel="noopener noreferrer" style={styles.link}>
                Read Full Article
              </a>
              {news.title && (
                <a
                  href={`https://www.youtube.com/results?search_query=${encodeURIComponent(news.title)}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={styles.link}
                >
                  Watch Related Video
                </a>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  newsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "15px",
  },
  newsCard: {
    backgroundColor: "#222",
    padding: "15px",
    borderRadius: "5px",
    textAlign: "left",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
    transition: "0.3s",
  },
  newsImage: {
    width: "100%",
    height: "150px",
    objectFit: "cover",
    borderRadius: "5px",
    marginBottom: "10px",
  },
  link: {
    color: "#00bcd4",
    textDecoration: "none",
    fontWeight: "bold",
    display: "block",
    marginTop: "10px",
    transition: "color 0.3s",
  },
};

export default DashboardPage;
