import React from "react";
import { useNavigate } from "react-router-dom";

const CategoriesPage = () => {
  const navigate = useNavigate();

  const categories = [
    { name: "Technology", image: "https://via.placeholder.com/150?text=Technology" },
    { name: "Sports", image: "https://via.placeholder.com/150?text=Sports" },
    { name: "Politics", image: "https://via.placeholder.com/150?text=Politics" },
    { name: "Business", image: "https://via.placeholder.com/150?text=Business" },
    { name: "Health", image: "https://via.placeholder.com/150?text=Health" },
    { name: "Entertainment", image: "https://via.placeholder.com/150?text=Entertainment" },
  ];

  const handleCategoryClick = (category) => {
    navigate(`/dashboard?category=${category}`);
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>Explore Categories</h1>
      <div style={styles.categories}>
        {categories.map((category) => (
          <div
            key={category.name}
            style={styles.categoryCard}
            onClick={() => handleCategoryClick(category.name)}
          >
            <img src={category.image} alt={category.name} style={styles.categoryImage} />
            <h3 style={styles.categoryName}>{category.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "15px",
  },
  categoryButton: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "0.3s",
    border: "none",
  },
};

export default CategoriesPage;
