import React from "react";
import { useNavigate } from "react-router-dom";

// Import static images
import technologyImage from "../assets/images/technology.jpg";
import sportsImage from "../assets/images/sports.jpg";
import politicsImage from "../assets/images/politics.jpg";
import businessImage from "../assets/images/business.jpg";
import healthImage from "../assets/images/health.jpg";
import entertainmentImage from "../assets/images/entertainment.jpg";

const CategoriesPage = () => {
  const navigate = useNavigate();

  const categories = [
    { name: "Technology", image: technologyImage },
    { name: "Sports", image: sportsImage },
    { name: "Politics", image: politicsImage },
    { name: "Business", image: businessImage },
    { name: "Health", image: healthImage },
    { name: "Entertainment", image: entertainmentImage },
  ];

  const handleCategoryClick = (category) => {
    navigate(`/dashboard?category=${category}`);
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>Explore Categories</h1>
      <div style={styles.categories}>
        {categories.map((category) => (
          <div
            key={category.name}
            style={styles.categoryCard}
            onClick={() => handleCategoryClick(category.name)}
          >
            <img src={category.image} alt={category.name} style={styles.categoryImage} />
            <h3 style={styles.categoryName}>{category.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "30px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  categories: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", // Increased min width for larger cards
    gap: "30px", // Increased gap for better spacing
    justifyContent: "center",
  },
  categoryCard: {
    backgroundColor: "#222",
    borderRadius: "15px", // Slightly larger border radius for a smoother look
    padding: "20px", // Increased padding for a more spacious card
    textAlign: "center",
    cursor: "pointer",
    transition: "transform 0.3s, box-shadow 0.3s",
    boxShadow: "0 6px 12px rgba(0, 0, 0, 0.3)", // Slightly larger shadow for emphasis
  },
  categoryCardHover: {
    transform: "scale(1.05)",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.4)", // Enhanced hover effect
  },
  categoryImage: {
    width: "100%",
    height: "200px", // Increased height for larger images
    objectFit: "cover",
    borderRadius: "10px",
    marginBottom: "15px", // Increased margin for better spacing
  },
  categoryName: {
    fontSize: "1.5rem", // Larger font size for category names
    fontWeight: "bold",
    color: "#fff",
  },
};

export default CategoriesPage;
