import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const NewsDetailsPage = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const title = queryParams.get("title");
  const image = queryParams.get("image");

  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/news/summary?title=${encodeURIComponent(title)}`
        );
        const data = await response.json();
        setSummary(data.summary || "No summary available.");
      } catch (error) {
        console.error("Error fetching summary:", error);
        setSummary("Failed to fetch summary.");
      } finally {
        setLoading(false);
      }
    };

    if (title) fetchSummary();
  }, [title]);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>{title}</h1>
      {image && <img src={image} alt="News" style={styles.image} />}
      <div style={styles.summaryContainer}>
        {loading ? <p style={styles.text}>Loading summary...</p> : <p style={styles.text}>{summary}</p>}
      </div>
    </div>
  );
};

const styles = {
  container: {
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    padding: "40px 20px",
    fontFamily: "'Segoe UI', sans-serif",
    maxWidth: "800px",
    margin: "0 auto",
    textAlign: "left",
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    borderBottom: "1px solid #444",
    paddingBottom: "10px",
  },
  image: {
    width: "100%",
    maxHeight: "400px",
    objectFit: "cover",
    borderRadius: "8px",
    marginBottom: "20px",
  },
  summaryContainer: {
    fontSize: "1.1rem",
    lineHeight: "1.6",
  },
  text: {
    whiteSpace: "pre-wrap",
  },
};

export default NewsDetailsPage;
