import React from "react";
import { Link } from "react-router-dom";

const HomePage = () => {
  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>Welcome to News Summarizer</h1>
      <p style={styles.description}>
        Stay updated with the latest news, summarized for your convenience. Explore categories, read summaries, and dive deeper into the topics that matter to you.
      </p>
      <div style={styles.navLinks}>
        <Link to="/about" style={styles.link}>About</Link>
        <Link to="/categories" style={styles.link}>Categories</Link>
        <Link to="/contact" style={styles.link}>Contact</Link>
        <Link to="/dashboard" style={styles.link}>Dashboard</Link>
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  description: {
    fontSize: "1.2rem",
    marginBottom: "30px",
    lineHeight: "1.6",
  },
  navLinks: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
    flexWrap: "wrap",
  },
  link: {
    padding: "10px 20px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    textDecoration: "none",
    fontWeight: "bold",
    transition: "0.3s",
  },
};

export default HomePage;
