import React, { useState } from "react";

const DashboardPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newsData, setNewsData] = useState([]);
  const [error, setError] = useState("");

  const categories = ["Technology", "Sports", "Politics", "Business", "Health", "Entertainment"];

  // Fetch summarized news from backend
  const fetchNews = async (query) => {
    setError("");
    setNewsData([]);
    try {
      const response = await fetch(`https://newsai-tibe.onrender.com/news/${query}`);
      const data = await response.json();

      if (!data.articles || data.articles.length === 0) {
        setError("No news found for this query.");
        return;
      }

      // Summarize each news article before displaying
      const summarizedNews = data.articles.map((news) => ({
        title: news.title,
        summary: summarizeText(news.description || "No description available"), // Summarization function
        imageUrl: news.urlToImage || "https://via.placeholder.com/300", // Default image if not available
        url: news.url,
      }));

      setNewsData(summarizedNews);
    } catch (err) {
      setError("Failed to fetch news. Try again.");
    }
  };

  // Simple summarization function (you can replace this with an API)
  const summarizeText = (text) => {
    if (!text) return "Summary not available.";
    return text.split(" ").slice(0, 20).join(" ") + "..."; // Truncate to 20 words
  };

  // Handle search event
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim() === "") return;
    fetchNews(searchTerm);
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>📰 News Dashboard</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={styles.searchContainer}>
        <input
          type="text"
          placeholder="Search for news..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Search</button>
      </form>

      {/* Categories */}
      <div style={styles.categories}>
        <h3 style={styles.sectionTitle}>Categories</h3>
        <div style={styles.buttonContainer}>
          {categories.map((category) => (
            <button key={category} style={styles.categoryBtn} onClick={() => fetchNews(category)}>
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* News Results */}
      <div style={styles.results}>
        {error && <p style={styles.error}>{error}</p>}
        {newsData.length > 0 && (
          <div>
            <h3 style={styles.sectionTitle}>Search Results</h3>
            <div style={styles.newsGrid}>
              {newsData.map((news, index) => (
                <div key={index} style={styles.newsCard}>
                  <img src={news.imageUrl} alt="News" style={styles.newsImage} />
                  <h4>{news.title}</h4>
                  <p>{news.summary}</p>
                  <div style={styles.buttonContainer}>
                    <a href={news.url} target="_blank" rel="noopener noreferrer" style={styles.newsLink}>
                      Read More →
                    </a>
                    <a
                      href={`https://www.youtube.com/results?search_query=${encodeURIComponent(news.title)}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      style={styles.videoLink}
                    >
                      🎥 Watch on YouTube
                    </a>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// **Styling**
const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  searchContainer: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    width: "300px",
    borderRadius: "5px",
    border: "1px solid #888",
    backgroundColor: "#222",
    color: "#fff",
    fontSize: "1rem",
  },
  button: {
    padding: "10px 15px",
    marginLeft: "10px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    border: "1px solid #fff",
    cursor: "pointer",
    fontWeight: "bold",
    transition: "0.3s",
  },
  buttonContainer: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "10px",
    marginTop: "10px",
  },
  categoryBtn: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    backgroundColor: "transparent",
    color: "#fff",
    cursor: "pointer",
    transition: "0.3s",
  },
  sectionTitle: {
    textTransform: "uppercase",
    fontSize: "1.2rem",
    marginBottom: "10px",
    letterSpacing: "1px",
    borderBottom: "2px solid #fff",
    display: "inline-block",
    paddingBottom: "5px",
  },
  results: {
    marginTop: "20px",
  },
  error: {
    color: "#ff4d4f",
    fontWeight: "bold",
  },
  newsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "15px",
    justifyContent: "center",
  },
  newsCard: {
    backgroundColor: "#222",
    padding: "15px",
    borderRadius: "5px",
    boxShadow: "0 2px 4px rgba(255, 255, 255, 0.1)",
    textAlign: "left",
  },
  newsImage: {
    width: "100%",
    borderRadius: "5px",
    marginBottom: "10px",
  },
  newsLink: {
    padding: "8px 10px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    textDecoration: "none",
    fontWeight: "bold",
    display: "inline-block",
  },
  videoLink: {
    padding: "8px 10px",
    borderRadius: "5px",
    backgroundColor: "#ff0000",
    color: "#fff",
    textDecoration: "none",
    fontWeight: "bold",
    display: "inline-block",
  },
};

export default DashboardPage;
