import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);
  const navigate = useNavigate(); // React Router's hook to navigate between pages

  useEffect(() => {
    // Function to update the isMobile state based on screen width
    const updateMobileView = () => {
      setIsMobile(window.innerWidth < 768);
    };

    // Initial check
    updateMobileView();

    // Add event listener on resize
    window.addEventListener("resize", updateMobileView);

    // Cleanup the event listener on component unmount
    return () => window.removeEventListener("resize", updateMobileView);
  }, []);

  // Function to handle login button click
  const handleLoginClick = () => {
    navigate("/login"); // Redirect to login page
  };

  // Function to handle signup button click
  const handleSignupClick = () => {
    navigate("/signup"); // Redirect to signup page
  };

  return (
    <nav style={styles.navbar}>
      {/* Logo */}
      <div style={styles.logo}>ðŸ“° News Summarizer</div>

      {/* Desktop Menu */}
      <ul style={{ ...styles.navLinks, display: isOpen && isMobile ? "flex" : "block" }}>
        <li style={styles.navItem}><Link to="/" style={styles.navLink}>Home</Link></li>
        <li style={styles.navItem}><Link to="/categories" style={styles.navLink}>Categories</Link></li>
        <li style={styles.navItem}><Link to="/about" style={styles.navLink}>About</Link></li>
        <li style={styles.navItem}><Link to="/contact" style={styles.navLink}>Contact</Link></li>
      </ul>

      {/* Login/Signup Button */}
      {!isMobile && (
        <div style={styles.authButtons}>
          <button style={styles.loginBtn} onClick={handleLoginClick}>Login</button>
          <button style={styles.signupBtn} onClick={handleSignupClick}>Sign Up</button>
        </div>
      )}

      {/* Hamburger Menu (Mobile View) */}
      {isMobile && (
        <div style={styles.hamburger} onClick={() => setIsOpen(!isOpen)}>
          â˜°
        </div>
      )}
    </nav>
  );
};

// **Minimalist Black & White Styling**
const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "15px 30px",
    backgroundColor: "#111",
    color: "#fff",
    fontFamily: "'Arial', sans-serif",
    position: "sticky",
    top: "0",
    width: "100%",
    zIndex: "1000",
    boxShadow: "0px 4px 8px rgba(255, 255, 255, 0.1)",
  },
  logo: {
    fontSize: "1.5rem",
    fontWeight: "bold",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  navLinks: {
    display: "flex",
    gap: "20px",
    listStyle: "none",
  },
  navItem: {
    display: "inline",
  },
  navLink: {
    color: "#fff",
    textDecoration: "none",
    fontSize: "1rem",
    fontWeight: "bold",
    padding: "5px 10px",
    transition: "0.3s",
  },
  authButtons: {
    display: "flex",
    gap: "10px",
  },
  loginBtn: {
    backgroundColor: "transparent",
    color: "#fff",
    border: "1px solid #fff",
    padding: "8px 12px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "0.3s",
  },
  signupBtn: {
    backgroundColor: "#fff",
    color: "#000",
    border: "none",
    padding: "8px 12px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "0.3s",
  },
  hamburger: {
    fontSize: "1.5rem",
    cursor: "pointer",
    display: "none",
  },
};

export default Navbar;
