import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Auth.css";

const Signup = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    password: "",
    phone: "",
  });

  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Password validation (6-8 characters, must contain numbers & special characters)
    const passwordRegex = /^(?=.*[0-9])(?=.*[!@#$%^&*])(.{6,8})$/;
    if (!passwordRegex.test(user.password)) {
      setError("Password must be 6-8 characters and include a number & special character!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/signup", user);
      if (response.data.success) {
        alert("Signup Successful! Please login.");
        navigate("/");
      }
    } catch (err) {
      setError(err.response.data.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>Sign Up</h2>
      {error && <p className="error">{error}</p>}
      <input type="text" name="username" placeholder="Username" onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
      <input type="text" name="phone" placeholder="Mobile Number" onChange={handleChange} required />
      <button onClick={handleSubmit}>Sign Up</button>
    </div>
  );
};

export default Signup;
