import React, { useState } from "react";

const DashboardPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newsData, setNewsData] = useState([]);
  const [error, setError] = useState("");
  const [showVideoLink, setShowVideoLink] = useState(null); // Track visibility of YouTube link

  // Categories for quick selection
  const categories = ["Technology", "Sports", "Politics", "Business", "Health", "Entertainment"];

  // Fetch summarized news from API
  // const fetchSummarizedNews = async (query) => {
  //   setError("");
  //   setNewsData([]);
  //   setSearchTerm(query);

  //   try {
  //     const response = await fetch(`https://newsai-tibe.onrender.com/api/summarize`, {
  //       method: "POST",
  //       headers: {
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify({ query }),
  //     });

  //     if (!response.ok) {
  //       throw new Error(`HTTP Error: ${response.status}`);
  //     }

  //     const data = await response.json();
  //     console.log("Summarized Data:", data);
  //     setNewsData(data.articles || []);
  //   } catch (err) {
  //     console.error("Error fetching summarized news:", err);
  //     setError("Failed to fetch summarized news. Try again.");
  //   }
  // };
  const fetchSummarizedNews = async (query) => {
    setError("");
    setNewsData([]);

    try {
        const response = await fetch(`http://localhost:5000/news//summarize`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ email: "test@example.com", password: "password123" }),
        });

        if (!response.ok) {
            throw new Error(`HTTP Error: ${response.status}`);
        }

        const data = await response.json();
        setNewsData(data.articles || []);
    } catch (err) {
        console.error("Error fetching summarized news:", err);
        setError("Failed to fetch summarized news. Try again.");
    }
};
  // Handle search manually
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim() === "") return;
    fetchSummarizedNews(searchTerm);
  };

  // Toggle the visibility of the YouTube link for each news item
  const toggleVideoLink = (index) => {
    setShowVideoLink((prevIndex) => (prevIndex === index ? null : index));
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>ðŸ“° News Summarizer</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={styles.searchContainer}>
        <input
          type="text"
          placeholder="Search for news..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Search</button>
      </form>

      {/* Categories */}
      <div style={styles.categories}>
        <h3 style={styles.sectionTitle}>Categories</h3>
        <div style={styles.buttonContainer}>
          {categories.map((category) => (
            <button key={category} style={styles.categoryBtn} onClick={() => fetchSummarizedNews(category)}>
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* News Results */}
      <div style={styles.results}>
        {error && <p style={styles.error}>{error}</p>}
        {newsData.length > 0 && (
          <div>
            <h3 style={styles.sectionTitle}>News Summaries</h3>
            <div style={styles.newsGrid}>
              {newsData.map((news, index) => (
                <div key={index} style={styles.newsCard}>
                  <img src={news.image} alt="News" style={styles.newsImage} />
                  <h4>{news.title}</h4>
                  <p>{news.summary}</p>
                  <button
                    onClick={() => window.open(news.url, "_blank")}
                    style={styles.newsLink}
                  >
                    Read Full News â†’
                  </button>
                  <button
                    onClick={() => toggleVideoLink(index)}
                    style={styles.videoButton}
                  >
                    {showVideoLink === index ? "Hide Related Video" : "Show Related Video"}
                  </button>
                  {showVideoLink === index && (
                    <a
                      href={`https://www.youtube.com/results?search_query=${encodeURIComponent(news.title)}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      style={styles.youtubeLink}
                    >
                      ðŸŽ¥ Watch Related Video
                    </a>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// **Styling**
const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#111",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  searchContainer: {
    display: "flex",
    justifyContent: "center",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    width: "300px",
    borderRadius: "5px",
    border: "1px solid #888",
    backgroundColor: "#222",
    color: "#fff",
    fontSize: "1rem",
  },
  button: {
    padding: "10px 15px",
    marginLeft: "10px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    color: "#000",
    border: "1px solid #fff",
    cursor: "pointer",
    fontWeight: "bold",
    transition: "0.3s",
  },
  buttonContainer: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "10px",
    marginBottom: "20px",
  },
  categories: {
    marginBottom: "20px",
  },
  categoryBtn: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    backgroundColor: "transparent",
    color: "#fff",
    cursor: "pointer",
    transition: "0.3s",
  },
  results: {
    marginTop: "20px",
  },
  error: {
    color: "#ff4d4f",
    fontWeight: "bold",
  },
  newsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "15px",
    justifyContent: "center",
  },
  newsCard: {
    backgroundColor: "#222",
    padding: "15px",
    borderRadius: "5px",
    boxShadow: "0 2px 4px rgba(255, 255, 255, 0.1)",
    textAlign: "left",
  },
  newsImage: {
    width: "100%",
    height: "150px",
    objectFit: "cover",
    borderRadius: "5px",
    marginBottom: "10px",
  },
  newsLink: {
    display: "block",
    marginTop: "10px",
    color: "#fff",
    textDecoration: "none",
    fontWeight: "bold",
    borderBottom: "1px solid #fff",
    width: "fit-content",
  },
  youtubeLink: {
    display: "block",
    marginTop: "5px",
    color: "#ff0000",
    textDecoration: "none",
    fontWeight: "bold",
  },
  videoButton: {
    padding: "8px 12px",
    borderRadius: "5px",
    border: "1px solid #fff",
    backgroundColor: "transparent",
    color: "#fff",
    cursor: "pointer",
    transition: "0.3s",
    marginTop: "10px",
  },
};

export default DashboardPage;
