import React from "react";
import { useNavigate } from "react-router-dom";

const CategoriesPage = () => {
  const navigate = useNavigate();

  const categories = [
    {
      name: "Technology",
      image: "https://cdn.pixabay.com/photo/2016/11/29/09/32/technology-1869308_960_720.jpg", // Technology-related image
    },
    {
      name: "Sports",
      image: "https://cdn.pixabay.com/photo/2016/11/29/03/53/soccer-1867123_960_720.jpg", // Sports-related image
    },
    {
      name: "Politics",
      image: "https://cdn.pixabay.com/photo/2016/11/08/05/26/capitol-1804350_960_720.jpg", // Political-related image
    },
    {
      name: "Business",
      image: "https://cdn.pixabay.com/photo/2017/08/10/03/47/stock-2616931_960_720.jpg", // Business-related image
    },
    {
      name: "Health",
      image: "https://cdn.pixabay.com/photo/2017/08/02/01/01/stethoscope-2578573_960_720.jpg", // Health-related image
    },
    {
      name: "Entertainment",
      image: "https://cdn.pixabay.com/photo/2016/03/09/09/17/cinema-1241422_960_720.jpg", // Entertainment-related image
    },
  ];

  const handleCategoryClick = (category) => {
    navigate(`/dashboard?category=${category}`);
  };

  return (
    <div style={styles.page}>
      <h1 style={styles.headline}>Explore Categories</h1>
      <div style={styles.categories}>
        {categories.map((category) => (
          <div
            key={category.name}
            style={styles.categoryCard}
            onClick={() => handleCategoryClick(category.name)}
          >
            <img src={category.image} alt={category.name} style={styles.categoryImage} />
            <h3 style={styles.categoryName}>{category.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  page: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    backgroundColor: "#000",
    color: "#fff",
    minHeight: "100vh",
    textAlign: "center",
  },
  headline: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "30px",
    textTransform: "uppercase",
    letterSpacing: "2px",
  },
  categories: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "20px",
    justifyContent: "center",
  },
  categoryCard: {
    backgroundColor: "#222",
    borderRadius: "10px",
    padding: "15px",
    textAlign: "center",
    cursor: "pointer",
    transition: "transform 0.3s, box-shadow 0.3s",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  },
  categoryCardHover: {
    transform: "scale(1.05)",
    boxShadow: "0 6px 12px rgba(0, 0, 0, 0.3)",
  },
  categoryImage: {
    width: "100%",
    height: "150px",
    objectFit: "cover",
    borderRadius: "10px",
    marginBottom: "10px",
  },
  categoryName: {
    fontSize: "1.2rem",
    fontWeight: "bold",
    color: "#fff",
  },
};

export default CategoriesPage;
